function Visible(selector,callback,callback2){if (!document.querySelector(selector)){throw "invalid selector";}this.target=document.querySelector(selector); this.callback=callback; this.callback2=callback2; this.options={root: null, rootMargin: '0px', threshold: 1.0};}Visible.prototype.watch=function(){var exec=this.callback; var exec2=this.callback2; function onChange(changes, observer){changes.forEach(change=>{if (change.isIntersecting){exec();}else{exec2();}});}; let observer=new IntersectionObserver(onChange, this.options); observer.observe(this.target);}